

/* burger-menu */

var btn = document.getElementById('navbar-toggler');
var menu = document.querySelector('.navbar-nav');
btn.onclick = function () {
    menu.classList.toggle('show');

};

/* slider - carousel */

var slideIndex = 1;
var slideInterval = 2000;
var switchInterval = setInterval(plusSlider, slideInterval);
showSlides(slideIndex);

function plusSlider() {
    showSlides(slideIndex += 1);
}

function minusSlide() {
    showSlides(slideIndex -= 1);
}

function currentSlide(n) {
    showSlides(slideIndex = n);
}

function showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("item");
    if (n > slides.length) {
        slideIndex = 1;
    }
    if (n < 1) {
        slideIndex = slides.length;
    }
    for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
    }
    slides[slideIndex - 1].style.display = "flex";
}

/* about creativia */
let selectedBtn;

document.querySelector('#accordion').onclick = function(event) {
  let target = event.target; 

  if (target.tagName != 'BUTTON') return; 

  highlight(target); 
};

function highlight(td) {
  if (selectedBtn) {
    selectedBtn.classList.remove('checked');
    document.querySelector('card-body').classList.remove('show'); 
  }
  selectedBtn = td;
  selectedBtn.classList.add('checked'); 
  document.querySelector('card-body').classList.add('show'); 
}

/* slider - clints */


var slideClintsIndex = 1;
var slideClintsInterval = 2000;
var switchClintshInterval = setInterval(plusClintsSlider, slideClintsInterval);
showClintsSlides(slideClintsIndex);

function currentClintsSlide(n) {
    showClintsSlides(slideClintsIndex = n);
}
function plusClintsSlider() {
    showClintsSlides(slideClintsIndex += 1);
}
function showClintsSlides(n) {
    var i;
    var slidesClints = document.getElementsByClassName("item-clints");
    var dots = document.getElementsByClassName("slider-dots_item");
    if (n > slidesClints.length) {
      slideIndex = 1
    }
    if (n < 1) {
        slideClintsIndex = slidesClints.length
    }
    for (i = 0; i < slidesClints.length; i++) {
        slidesClints[i].style.display = "none";
    }
    for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
    }
    slidesClints[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
}
